
public class ClickableOyster extends InteractiveObject{
	
	public ClickableOyster(int x, int y){
		this.xpos = x;
		this.ypos = y;
	}

	public int getX() {
		// TODO Auto-generated method stub
		return this.xpos;
	}

	public int getY() {
		// TODO Auto-generated method stub
		return this.ypos;
	}

}
